var layouts=["background","hero","profile","page","card"],currentLayout=4;function switchHomeLayout(){var e,t,n=currentLayout;currentLayout=currentLayout==layouts.length-1?0:currentLayout+1,e=document.getElementById(layouts[n]),t=document.getElementById(layouts[currentLayout]);const s=document.querySelectorAll("code[id=layout]");t&&(t.style.display="block"),e&&(e.style.display="none"),s.forEach(function(e){e.innerText=layouts[currentLayout]})}window.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("#switch-layout-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchHomeLayout()}))})